import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from "axios";

class App extends Component {

  state = {
    searchResults: []
  }

  myOwnMethod = (event) => {
    this.setState({
      searchTerm: event.target.value
    })
  }

  submitSearch = (e) => {
    e.preventDefault();
    console.log(this.state.searchTerm);
    axios.get(`https://www.googleapis.com/books/v1/volumes?q=${this.state.searchTerm}`)
      .then(response => {
        console.log(response);
        this.setState({
          searchResults: response.data.items
        })
      })
      .catch(function (error) {
        console.log(error);
      });

  }

  render() {
    return (
      <div className="App">
        <h1>React Book Search</h1>
        <form onSubmit={this.submitSearch}>
          <div className="form-group">
            <input onChange={this.myOwnMethod} placeholder="Search Term"></input> <button type="submit" class="btn btn-primary">Search</button>
          </div>
        </form>
        {this.state.searchResults.map(each =>
          <div>
            <h2>Title: {each.volumeInfo.title}</h2>
            <p className="book-header">
            {each.volumeInfo.subtitle}<br />{each.volumeInfo.subtitle}<br />
            </p>
            <hr width="70%" />
          </div>
        )}
      </div>
    );
  }
}
//don't forget to get id of book when
export default App;
